// Header.jsx
import React, { useContext, useState, useEffect } from 'react';
import { ThemeContext } from '../context/ThemeContext';
import { Link } from 'react-router-dom';
import { FaSun, FaMoon, FaBars, FaTimes } from 'react-icons/fa';

function Header() {
  const { darkMode, setDarkMode } = useContext(ThemeContext);
  const [menuOpen, setMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  // Detect scroll to toggle header background
  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 10);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header
      className={`fixed top-0 w-full z-50 transition-all duration-300 ${
        scrolled ? 'bg-blue-600 shadow' : 'bg-transparent'
      } text-white`}
    >
      <div className="max-w-7xl mx-auto flex items-center justify-between px-6 py-4">
        {/* Logo */}
        <h1 className="text-2xl font-bold">üèçÔ∏è Bike Rentals</h1>

        {/* Mobile Menu Toggle */}
        <button
          className="sm:hidden text-2xl"
          onClick={() => setMenuOpen(!menuOpen)}
          aria-label="Toggle Menu"
        >
          {menuOpen ? <FaTimes /> : <FaBars />}
        </button>

        {/* Navigation */}
        <nav
          className={`${
            menuOpen ? 'translate-y-0 opacity-100' : 'translate-y-[-20px] opacity-0 sm:opacity-100 sm:translate-y-0'
          } sm:flex flex-col sm:flex-row gap-4 text-lg sm:items-center sm:static absolute top-full left-0 w-full sm:w-auto bg-blue-600 sm:bg-transparent px-6 py-4 sm:p-0 transition-all duration-300`}
        >
          <Link to="/" className="hover:underline hover:text-gray-200 transition">Home</Link>
          <Link to="/book" className="hover:underline hover:text-gray-200 transition">Book</Link>
          <Link to="/rent" className="hover:underline hover:text-gray-200 transition">Rent Out</Link>
          <Link to="/profile" className="hover:underline hover:text-gray-200 transition">Profile</Link>

          {/* Dark Mode Toggle */}
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="flex items-center gap-2 px-3 py-1 border border-white rounded bg-white text-blue-600 hover:bg-gray-100 transition"
            aria-label="Toggle Dark Mode"
          >
            {darkMode ? <FaSun /> : <FaMoon />}
            {darkMode ? 'Light' : 'Dark'}
          </button>

          {/* User Avatar + Logout */}
          <div className="flex items-center gap-3 mt-4 sm:mt-0">
            <img
              src="/avatar.png"
              alt="User Avatar"
              className="w-8 h-8 rounded-full border border-white"
            />
            <button className="px-3 py-1 bg-white text-blue-600 rounded hover:bg-gray-100 transition">
              Logout
            </button>
          </div>
        </nav>
      </div>
    </header>
  );
}

export default Header;
