// src/components/Sidebar.jsx
import React, { useState } from 'react';
import { useNavigate, NavLink } from 'react-router-dom';

function Sidebar() {
  const [location, setLocation] = useState('');
  const [date, setDate] = useState('');
  const [sort, setSort] = useState('');
  const navigate = useNavigate();

  const handleBookClick = () => {
    const query = new URLSearchParams();
    if (location) query.set('location', location);
    if (date) query.set('date', date);
    if (sort) query.set('sort', sort);
    navigate(`/book?${query.toString()}`);
  };

  return (
    <aside className="w-64 bg-gray-100 dark:bg-gray-900 p-4 shadow-lg sticky top-0 h-full">
      <h2 className="text-xl font-bold mb-4 text-blue-600 dark:text-blue-300">üìç Explore</h2>
      <input
        type="text"
        placeholder="Search location"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
        className="w-full mb-2 px-3 py-2 border rounded dark:bg-gray-800 dark:text-white"
      />
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        className="w-full mb-2 px-3 py-2 border rounded dark:bg-gray-800 dark:text-white"
      />
      <select
        value={sort}
        onChange={(e) => setSort(e.target.value)}
        className="w-full mb-4 px-3 py-2 border rounded dark:bg-gray-800 dark:text-white"
      >
        <option value="">Sort</option>
        <option value="price">Sort by Price</option>
        <option value="model">Sort by Model</option>
      </select>

      <nav className="flex flex-col gap-3">
        <button onClick={handleBookClick} className="text-blue-500 hover:underline text-left">
          Book a Bike
        </button>
        <NavLink
          to="/rent"
          className={({ isActive }) =>
            isActive ? 'text-blue-700 font-semibold' : 'text-blue-500 hover:underline'
          }
        >
          Rent Your Bike
        </NavLink>
        <NavLink
          to="/profile"
          className={({ isActive }) =>
            isActive ? 'text-blue-700 font-semibold' : 'text-blue-500 hover:underline'
          }
        >
          Profile
        </NavLink>
      </nav>
    </aside>
  );
}

export default Sidebar;
