import React, { useEffect, useState } from 'react';

function Home() {
  const [darkMode, setDarkMode] = useState(false);

  // Apply/remove dark class on <html>
  useEffect(() => {
    const root = window.document.documentElement;
    if (darkMode) {
      root.classList.add('dark');
    } else {
      root.classList.remove('dark');
    }
  }, [darkMode]);

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-800 dark:text-gray-100 transition-colors duration-300 font-sans">
      
      {/* Header */}
      <header className="bg-white dark:bg-gray-800 shadow-md py-4 px-6 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-indigo-600 dark:text-indigo-400">üèçÔ∏è Bike Rentals</h1>
        <div className="flex items-center gap-4">
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="px-3 py-1 rounded bg-gray-200 dark:bg-gray-700 text-sm text-gray-800 dark:text-gray-100 hover:bg-gray-300 dark:hover:bg-gray-600 transition"
          >
            {darkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
          </button>
          <button className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700 transition">
            Book Now
          </button>
          <img
            src="/avatar.png"
            alt="User Avatar"
            className="w-10 h-10 rounded-full border-2 border-indigo-500"
          />
        </div>
      </header>

      {/* Welcome Section */}
      <section className="text-center px-6 py-10">
        <h2 className="text-4xl font-bold text-indigo-600 dark:text-indigo-400 mb-4 flex items-center justify-center gap-2">
          üèçÔ∏è Welcome to <span className="text-black dark:text-white">Bike Rentals</span>
        </h2>
        <p className="text-lg text-gray-700 dark:text-gray-300 max-w-xl mx-auto">
          Use the sidebar to book or rent out bikes in your city. Whether you're commuting, exploring, or just cruising ‚Äî we've got the perfect ride for you.
        </p>
      </section>

      {/* Bike Cards */}
      <section className="px-6 py-8 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 max-w-6xl mx-auto">
        {['Yamaha R15', 'Royal Enfield', 'KTM Duke'].map((bike, index) => (
          <div key={index} className="bg-white dark:bg-gray-800 rounded-lg shadow hover:shadow-lg transition p-4">
            <img
              src={`/bikes/${bike.toLowerCase().replace(/ /g, '-')}.jpg`}
              alt={bike}
              className="w-full h-40 object-cover rounded"
            />
            <h3 className="text-xl font-semibold mt-4">{bike}</h3>
            <p className="text-gray-600 dark:text-gray-300">‚Çπ500/day</p>
            <button className="mt-4 w-full bg-indigo-600 text-white py-2 rounded hover:bg-indigo-700 transition">
              Rent Now
            </button>
          </div>
        ))}
      </section>

      {/* Footer */}
      <footer className="px-6 py-4 text-center text-sm text-gray-500 dark:text-gray-400">
        ¬© 2025 Likithasri Rentals. All rights reserved.
      </footer>
    </div>
  );
}

export default Home;
